        /// <summary>
        /// テキストから URL を抽出して返します
        /// </summary>
        public static IEnumerable<string> ExtractUrls(string text)
            => ExtractUrlEntities(text).Select(x => x.Url);

        /// <summary>
        /// テキストから URL を抽出してエンティティとして返します
        /// </summary>
        public static IEnumerable<TwitterEntityUrl> ExtractUrlEntities(string text)
        {
            var urlMatches = Regex.Matches(text, Twitter.RgUrl, RegexOptions.IgnoreCase).Cast<Match>();
            foreach (var m in urlMatches)
            {
                var before = m.Groups["before"].Value;
                var url = m.Groups["url"].Value;
                var protocol = m.Groups["protocol"].Value;
                var domain = m.Groups["domain"].Value;
                var path = m.Groups["path"].Value;

                var validUrl = false;
                if (protocol.Length == 0)
                {
                    if (Regex.IsMatch(before, Twitter.UrlInvalidWithoutProtocolPrecedingChars))
                        continue;

                    var last_url_invalid_match = false;
                    var domainMatches = Regex.Matches(domain, Twitter.UrlValidAsciiDomain, RegexOptions.IgnoreCase).Cast<Match>();
                    foreach (var mm in domainMatches)
                    {
                        var lasturl = mm.Value;
                        last_url_invalid_match = Regex.IsMatch(lasturl, Twitter.UrlInvalidShortDomain, RegexOptions.IgnoreCase);
                        if (!last_url_invalid_match)
                        {
                            validUrl = true;
                        }
                    }

                    if (last_url_invalid_match && path.Length != 0)
                    {
                        validUrl = true;
                    }
                }
                else
                {
                    validUrl = true;
                }

                if (validUrl)
                {
                    var urlGroup = m.Groups["url"];
                    var startPos = text.GetCodepointCount(0, urlGroup.Index);
                    var endPos = startPos + text.GetCodepointCount(urlGroup.Index, urlGroup.Index + urlGroup.Length);

                    yield return new TwitterEntityUrl
                    {
                        Indices = new[] { startPos, endPos },
                        Url = url,
                        ExpandedUrl = url,
                        DisplayUrl = url,
                    };
                }
            }
        }

        /// <summary>
        /// テキストからメンションを抽出してエンティティとして返します
        /// </summary>
        public static IEnumerable<TwitterEntityMention> ExtractMentionEntities(string text)
        {
            // リスト
            var matchesAtList = Regex.Matches(text, @"(?<=^|[^a-zA-Z0-9_/])([@＠][a-zA-Z0-9_]{1,20}/[a-zA-Z][a-zA-Z0-9\p{IsLatin-1Supplement}\-]{0,79})");
            foreach (var match in matchesAtList.Cast<Match>())
            {
                var startPos = text.GetCodepointCount(0, match.Index);
                var endPos = startPos + text.GetCodepointCount(match.Index, match.Index + match.Length);

                yield return new TwitterEntityMention
                {
                    Indices = new[] { startPos, endPos },
                    ScreenName = match.Value.Substring(1), // 先頭の「@」は取り除く
                };
            }

            // 通常のメンション
            var matchesAtUser = Regex.Matches(text, "(?<=^|[^a-zA-Z0-9_/])([@＠][a-zA-Z0-9_]{1,20})(?=[^a-zA-Z0-9_/]|$)");
            foreach (var match in matchesAtUser.Cast<Match>())
            {
                var startPos = text.GetCodepointCount(0, match.Index);
                var endPos = startPos + text.GetCodepointCount(match.Index, match.Index + match.Length);

                yield return new TwitterEntityMention
                {
                    Indices = new[] { startPos, endPos },
                    ScreenName = match.Value.Substring(1), // 先頭の「@」は取り除く
                };
            }
        }

        /// <summary>
        /// テキストからハッシュタグを抽出してエンティティとして返します
        /// </summary>
        public static IEnumerable<TwitterEntityHashtag> ExtractHashtagEntities(string text)
        {
            var matches = Regex.Matches(text, Twitter.Hashtag);
            foreach (var match in matches.Cast<Match>())
            {
                var groupHashtagSharp = match.Groups[2];
                var groupHashtagText = match.Groups[3];
                var startPos = text.GetCodepointCount(0, groupHashtagSharp.Index);
                var endPos = startPos + text.GetCodepointCount(groupHashtagSharp.Index, groupHashtagSharp.Index + groupHashtagSharp.Length + groupHashtagText.Length);

                yield return new TwitterEntityHashtag
                {
                    Indices = new[] { startPos, endPos },
                    Text = groupHashtagText.Value,
                };
            }
        }

        /// <summary>
        /// テキストから絵文字を抽出して <see cref="TwitterEntityEmoji"/> として返します
        /// </summary>
        public static IEnumerable<TwitterEntityEmoji> ExtractEmojiEntities(string text)
        {
            var matches = TweetExtractor.EmojiPattern.Matches(text);

            foreach (var match in matches.Cast<Match>())
            {
                var input = match.Value;

                // 異字体セレクタ U+FE0F (emoji style) は除去する (ZWJ を含まない場合のみ)
                if (!input.Contains('\u200D'))
                    input = input.Replace("\uFE0F", "");

                var codepointHex = new List<string>();

                for (var i = 0; i < input.Length; i += char.IsSurrogatePair(input, i) ? 2 : 1)
                {
                    var codepoint = char.ConvertToUtf32(input, i);
                    codepointHex.Add($"{codepoint:x}");
                }

                var startPos = text.GetCodepointCount(0, match.Index);
                var endPos = startPos + text.GetCodepointCount(match.Index, match.Index + match.Length);

                TwitterEntityEmoji entity;
                if (codepointHex.Count >= 1)
                {
                    entity = new TwitterEntityEmoji
                    {
                        Indices = new[] { startPos, endPos },
                        Text = input,
                        Url = "https://twemoji.maxcdn.com/2/72x72/" + string.Join("-", codepointHex) + ".png",
                    };
                }
                else
                {
                    entity = new TwitterEntityEmoji
                    {
                        Indices = new[] { startPos, endPos },
                        Text = input,
                        Url = "",
                    };
                }

                yield return entity;
            }
        }
    }
